import React, { useEffect, useState } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';

const App = () => {
  const [numJokes, setNumJokes] = useState(100);
  const [jokes, setJokes] = useState([]);
  const [query, setQuery] = useState('');


  const getRandomQ = (e) => {
    e.preventDefault();
    fetch(`https://api.chucknorris.io/jokes/search?query=${query}`)
      .then(res => res.json())
      .then(json => setJokes(json.result))
      .catch(err => console.log(err))
    setQuery('');
  }



  const onChangeHandler = (e) => {
    setQuery(e.target.value);
  }

  return (
    <>
      <header>
        <h1>Chuck Noriss</h1>
      </header>
      <main>

        <Tabs>
          <TabList>
            <Tab>Random</Tab>
            <Tab>Search</Tab>
          </TabList>

          <TabPanel>
            <h2>Any content 1</h2>
          </TabPanel>
          <TabPanel>
            <form onSubmit={getRandomQ}>
              <input type="text" onChange={onChangeHandler} value={query} />
            </form>

            <>
              {jokes?.map((elm, index) =>
                <li key={index}>{elm.value}</li>
              )}
            </>
          </TabPanel>
        </Tabs>

      </main>
    </>
  )
}

export default App;
