import React, { useEffect, useState } from 'react'
import './index.css'

export default function index({ props }) {
    props.tab == 0 ? () => {
        const [categories, setCategories] = useState([]);
        useEffect(() => {
            fetch(`https://api.chucknorris.io/jokes/categories`)
                .then(res => res.json())
                .then(json => setCategories(json))
                .catch(err => console.log(err))
        }, [])

        return (
            <div className="tabHeader">
                <form onSubmit={props.byCategory} className="form">
                    <div>
                        <span className="label"><b>Your name</b></span>
                        <input type="text" className="input" placeholder="Chuck Norris" id="name" onChange={(e) => props.setName(e.target.value)} value={props.name} />
                    </div>

                    <div>
                        <span className="label"><b>Categories</b></span>
                        <select id="category" className="input" onChange={e => props.setCategory(e.target.value)}>
                            <option value="-">All categories</option>
                            {categories?.map((elm, index) => (
                                <option key={index} value={elm}>{elm.charAt(0).toUpperCase() + elm.slice(1)}</option>
                            )
                            )}
                        </select>
                    </div>
                    <div>
                        <button>GO!</button>
                    </div>
                </form>
            </div>
        )
    } : null
