import React, { useEffect, useState } from 'react'

const App = () => {
  const [numJokes, setNumJokes] = useState(100);
  const [jokes, setJokes] = useState([]);


  const getRandomQ = (str) => {
    fetch(`https://api.chucknorris.io/jokes/search?query=${str}`)
      .then(res => res.json())
      .then(json => setJokes(json.result))
      .catch(err => console.log(err))
  }

  const displayJokes = () => {
    jokes.map((elm, index) =>
      <li key={index}>{elm.value}</li>
    )
  }

  return (
    <>
      <header>
        <h1>Chuck Noriss</h1>
      </header>
      <main>
        <form onSubmit={getRandomQ}>
          <input type="text" />
        </form>

        <>
          {displayJokes}
        </>

      </main>
    </>
  )
}

export default App;
