import React, { useEffect, useState } from 'react'

const App = () => {
  const [numJokes, setNumJokes] = useState(100);
  const [jokes, setJokes] = useState([]);

  useEffect(() => {
    fetch(`https://api.chucknorris.io/jokes/categories`)
      .then(res => res.json())
      .then(results => displayJokes(results))
      .catch(err => console.log(err))
  }, [])

  const displayJokes = (data) => {
    setJokes(data)
  }

  return (
    <>
      <header>
        <h1>Chuck Noriss</h1>
      </header>
      <main>
        {
          jokes.map((joke, index) => {
            return (
              <p key={joke.id}>{index+1}) {joke.joke}</p>
            )
          })
        }
      </main>
    </>
  )
}

export default App;
