import React, { useEffect, useState } from 'react'

const App = () => {
  const [numJokes, setNumJokes] = useState(100);
  const [jokes, setJokes] = useState([]);
  const [query, setQuery] = useState('');


  const getRandomQ = () => {
    fetch(`https://api.chucknorris.io/jokes/search?query=${query}`)
      .then(res => res.json())
      .then(json => setJokes(json.result))
      .catch(err => console.log(err))
      setQuery('');
  }

  const displayJokes = () => {
    jokes.map((elm, index) =>
      <li key={index}>{elm.value}</li>
    )
  }

  const onChangeHandler = (e) => {
    e.preventDefault();
    setQuery(e.target.value);
  }

  return (
    <>
      <header>
        <h1>Chuck Noriss</h1>
      </header>
      <main>
        <form onSubmit={getRandomQ}>
          <input type="text" onChange={onChangeHandler} value={query} />
        </form>

        <>
          {displayJokes}
        </>

      </main>
    </>
  )
}

export default App;
