import React, { useEffect, useState } from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';

const App = () => {

  const [jokes, setJokes] = useState([]);
  const [query, setQuery] = useState('');
  const [name, setName] = useState('');
  const [category, setCategory] = useState('');
  const [randomJoke, setRandomJoke] = useState('');
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch(`https://api.chucknorris.io/jokes/categories`)
      .then(res => res.json())
      .then(json => setCategories(json))
      .catch(err => console.log(err))
  }, [])

  const search = (e) => {
    e.preventDefault();
    fetch(`https://api.chucknorris.io/jokes/search?query=${query}`)
      .then(res => res.json())
      .then(json => setJokes(json.result))
      .catch(err => console.log(err))
    setQuery('');
  }

  const byCategory = (e) => {
    console.log(e.target.select)
    e.preventDefault();
    fetch(`https://api.chucknorris.io/jokes/random`)
      .then(res => res.json())
      .then(json => setRandomJoke(json.value))
      .catch(err => console.log(err))
    setQuery('');
  }
  // ?category={category}


  return (
    <>
      <header>
        <h1>Chuck Noriss</h1>
      </header>
      <main>

        <Tabs>
          <TabList>
            <Tab>Random</Tab>
            <Tab>Search</Tab>
          </TabList>

          <TabPanel>
            <form onSubmit={byCategory}>
              <lable>Your name</lable>
              <input type="text" onChange={(e) => setName(e.target.value)} value={query} />
              <label>Categories</label>
              <select onChange={ e => setCategory(e.selected.value)}>
                <option value="-">All categories</option>
                {categories?.map((elm, index) => (
                  <option key={index} value={elm}>{elm.charAt(0).toUpperCase() + elm.slice(1)}</option>
                )
                )}
              </select>
              <button>GO</button>
            </form>
            <br/>
            <>
                {randomJoke}
            </>
          </TabPanel>
          <TabPanel>
            <form onSubmit={search}>
              <input type="text" onChange={(e) => setQuery(e.target.value)} value={query} />
            </form>

            <>
              {jokes?.map((elm, index) =>
                <li key={index}>{elm.value}</li>
              )}
            </>
          </TabPanel>
        </Tabs>

      </main>
    </>
  )
}

export default App;
